mapper.select:
select
id, parent_id, name, status, sort_order, is_parent, created, updated
from e3mall.tb_content_category


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and status = #{status}


        and sort_order = #{sortOrder}


        and is_parent = #{isParent}


        and created = #{created}


        and updated = #{updated}
